package fourWayStreetLights.util;

import java.io.File;
import java.io.FileNotFoundException;
import java.io.PrintStream;

public class Results implements StdoutDisplayInterface, FileDisplayInterface  {
	public void writeToStdout(String s) {

//		System.out.println("The Sum of all elements in the array list is:  " + s);
		System.out.println("This functionality is not required in this assignment");

	}
	/**
	 * method takes the input and print the input in a file which is generated by it
	 * 
	 * @param s. s is a string which will be printed in the output.txt file.
	 */
	public void writeToFile(String s) {
		try {
			PrintStream myconsole = new PrintStream(new File("output.txt"));
			System.setOut(myconsole);
			myconsole.print(s);
		} catch (FileNotFoundException e) {
			System.out.println(e);
		}
	}

}